string reduct(string T) {
	string ans;
	for (char ch : T) {
		if (ch == 'R') {
			ans += ch;
		} else if (!ans.empty() && ans.back() == 'S') {
			ans.pop_back();
		} else {
			ans += ch;
		}
	}
	return ans;
}

void putans() {
	int from = 0, to = 0;
	repr(i,N-1,0) {
		from = from * 3 + curd[i];
		to = to * 3 + ansd[i];
	}
	anss[from] = to;
}

void solve(int digit, string T) {
	if (digit == N) return putans();

	for (int d : {0, 1, 2}) {
		int now = d;
		string nt;
		for (char c : T) {
			if (c == 'S') {
				nt += c;
				if(now) now= 3-now;
			}
            else{
				++now;
				if (now == 3) {
					nt += c;
					now = 0;
				}
			}
		}
		curd[digit] = d; // 
		ansd[digit] = now;
		solve(digit + 1, reduct(nt));
	}
}
