public:

    int calc(vector<vector<vector<int>>>& v, int k) {
      int w = 0;
      if (k > 0) {
        w = calc(v, k-1);
      }
      auto ret = v[2][k][w];

      auto w0 = v[0][k][w];
      auto w1 = v[1][k][w];
      auto w2 = v[2][k][w];

      v[1][k][w] = w0;
      v[2][k][w] = w1;
      v[0][k][w] = w2;
      return ret;
    }

