const int SZ = 1;
int n;
int pow3n;
vector<int> sal;
void init_s(){
    sal.resize(pow3n);
    iota(sal.begin(),sal.end(),0);
    for(int i=0;i<pow3n;i++){
        int x = i;
        int tar = 0;
        int r = 1;
        for(int j=0;j<n;j++){
            tar+= (x%3*2)%3*r;
            x/=3;
            r*=3;
        }
        if(tar<i){
            // cerr<<i<<" "<<tar<<endl;
            swap(sal[tar],sal[i]);
        }
    }
}


void do_r(vector<int>& pat){
    int head = pat[pow3n-1];
    for(int i=pow3n-1;i>0;i--){
        pat[i]=pat[i-1];
    }
    pat[0] = head;
}
int ary[100000];
void perm(vector<int>& pat,vector<int>& par){
    for(int i=0;i<pow3n;i++) ary[i] = pat[par[i]];
    for(int i=0;i<pow3n;i++) pat[i] = ary[i];
}
void do_s(vector<int>& pat){
    perm(pat,sal);
}
int main(){
    cin>>n;
    pow3n=1;
    for(int i=0;i<n;i++) pow3n*=3;
    init_s();

    string t;
    {
        string tmp;
        cin>>tmp;
        for(auto c:tmp){
            if(!t.empty() && t.back()=='S' && c=='S') t.pop_back();
            else t+=c;
        }
    }

    vector<vector<int>> memo(1<<SZ);

    for(int bit=0;bit<(1<<SZ);bit++){
        bool ispass= false;
        for(int j=0;j+1<SZ;j++){
            if(((bit>>j)&1)==0 && ((bit>>(j+1)))==0) ispass=true;
        }
        if(ispass) continue;

        memo[bit].resize(pow3n);
        iota(memo[bit].begin(),memo[bit].end(),0);
        for(int j=0;j<SZ;j++){
            if(((bit>>j)&1)==0) do_s(memo[bit]);
            else do_r(memo[bit]);
        }
    }
    vector<int> res(pow3n);
    iota(res.begin(),res.end(),0);

    for(int i=0;i<t.size()/SZ;i++){
        int bit = 0;
        for(int j=0;j<SZ;j++){
            if(t[i*SZ+j]=='R') bit+=(1<<j);
        }
        // cerr<<"#"<<i<<endl;
        // for(int i=0;i<pow3n;i++) cerr<<res[i]<<" ";
        // cerr<<endl;
        // for(int i=0;i<pow3n;i++) cerr<<memo[bit][i]<<" ";
        // cerr<<endl;
        perm(res,memo[bit]);
        // for(int i=0;i<pow3n;i++) cerr<<res[i]<<" ";
        // cerr<<endl;
    }

    for(int i=t.size()/SZ*SZ;i<t.size();i++){
        // cerr<<"#"<<endl;
        if(t[i]=='S') do_s(res);
        else do_r(res);
    }
    vector<int> inv(pow3n);
    for(int i=0;i<pow3n;i++) inv[res[i]]=i;
    for(int i=0;i<pow3n;i++) cout<<inv[i]<<" ";
    cout<<endl;


    return 0;
}

