            }
            if(ch=='R')
            {
                curd++;
                if(curd==3)
                {
                    curd=0;
                    curt+='R';
                }
            }
        }
        curdigit[cur]=d;
        curres[cur]=curd;
        solve(cur+1,reduce(curt));
    }
}
