void runLengthCompression(string& src, vector<pair<char, int>>& dest) {
	dest.clear();
	int N = src.size();
	if (N == 0) { return; }

	int count = 1;
	char temp = src[0];
	for (int i = 1; i <= N; i++) {
		if (i == N) {
			dest.push_back({ temp, count });
		}
		else {
			if (src[i] != temp) {
				dest.push_back({ temp, count });
				temp = src[i];
				count = 1;
			}
			else {
				count++;
			}
		}
	}
}

