vector<int> ans;

string compress(const string &t) {
	string ret = "";

	for(auto &elem: t) {
		if (elem == 'R') {
			ret += elem;
		} else {
			if(ret.size() and ret.back() == 'S') {
				ret.pop_back();
			} else {
				ret += elem;
			}
		}
	}

	return ret; 
}

int n, fd[12], td[12];

void rec(int digit, string t) {
	if(digit == n) {
		int from = 0, to = 0;
		rrep(i, n) {
			from = 3 * from + fd[i];
			to = 3 * to + td[i];
		}
		ans[from] = to;
		return;
	}


	rep(d, 3) {
		int cur_d = d;

		string nxt_t = "";
		for(auto &elem: t) {
			if(elem == 'S') {
				cur_d = (3 - cur_d) % 3;
				nxt_t += elem;
			} else {
				cur_d++;
				if(cur_d == 3) {
					nxt_t += elem;
					cur_d = 0;
				}
			}
		}

		fd[digit] = d;
		td[digit] = cur_d;
		rec(digit + 1, compress(nxt_t));
	}
}

