#include <bits/stdc++.h>
using namespace std;

#define ll long long
#define all(aaa) aaa.begin(), aaa.end()

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);

    string s;
    int n;
    cin >> n >> s;

    int H[2] = {1, 1}, D[2] = {(n + 1) / 2, n / 2};

    for (int i = 0; i < 2; i++) {
        for (int j = 0; j < D[i]; j++) {
            H[i] *= 3;
        }
    }

    vector<int> ops[2], opt[2];
    vector<string> shorten_s(H[0]);
    vector<int> res(H[0]);

    for (int j = 0; j < 2; j++) {
        ops[j].resize(H[j]);
        opt[j].resize(H[j]);

        for (int i = 0; i < H[j]; i++)  {
            int x = i;

            vector<int> v;
            while (x > 0) {
                v.push_back(x % 3);
                x /= 3;
            }

            reverse(all(v));
            for (int y : v) {
                ops[j][i] *= 3;
                if (y > 0) {
                    y = 3 - y;
                    ops[j][i] = ops[j][i] + y;
                }
            }

            opt[j][i] = (i + 1) % H[j];
        }
    }

    // cout << opt[0][2] << "\n";

    for (int i = 0; i < H[0]; i++) {
        res[i] = i;
        for (const char &c : s) {
            if (c == 'S') {
                if (!shorten_s[i].empty() && shorten_s[i].back() == c) {
                    shorten_s[i].pop_back();
                }
                else {
                    shorten_s[i].push_back(c);
                }
                res[i] = ops[0][res[i]];
            }
            else {
                res[i] = opt[0][res[i]];
                if (res[i] == 0)
                    shorten_s[i].push_back(c);
            }
        }
    }

    for (int i = 0; i < H[0] * H[1]; i++) {
        ll ans = i / H[0];
        for (const char &c : shorten_s[i % H[0]]) {
            if (c == 'S') {
                ans = ops[1][ans];
            }
            else {
                ans = opt[1][ans];
            }
        }
        ans = ans * H[0] + res[i % H[0]];
        cout << ans << " ";
    }

    return 0;
}

