#include<bits/stdc++.h>
using namespace std;
int ch[10000005][3],ed[10000005],cnt=1,ans[10000005];
bool rev[10000005];
char s[200005];
void pushr(int x){swap(ch[x][1],ch[x][2]);rev[x]^=1;}
void pushdown(int x)
{
    if(!rev[x])return;
    for(int i=0;i<=2;i++)if(ch[x][i])pushr(ch[x][i]);
    rev[x]=0;
}
void dfs(int x)
{
    pushdown(x);
    swap(ch[x][0],ch[x][2]),swap(ch[x][1],ch[x][2]);
    if(ch[x][0])dfs(ch[x][0]);
}
void get(int x,int now,int pw)
{
    pushdown(x);
    if(ed[x]!=0xffffffff)ans[ed[x]]=now;
    if(ch[x][0])get(ch[x][0],now,pw*3);
    if(ch[x][1])get(ch[x][1],now+pw,pw*3);
    if(ch[x][2])get(ch[x][2],now+2*pw,pw*3);
}
int main()
{
    int n,m=1;
    scanf("%d",&n);
    for(int i=1;i<=n;i++)m*=3;
    memset(ed,0xff,sizeof(ed));
    for(int i=0;i<m;i++)
    {
        int now=1,tmp=i;
        for(int k=0;k<n;k++)
        {
            int t=tmp%3;
            tmp/=3;
            if(!ch[now][t])ch[now][t]=++cnt;
            now=ch[now][t];
        }
        ed[now]=i;
    }
    scanf("%s",s+1);
    int q=strlen(s+1);
    for(int i=1;i<=q;i++)
    {
        if(s[i]=='S')pushr(1);
        else dfs(1);
    }
    get(1,0,1);
    for(int i=0;i<m;i++)printf("%d ",ans[i]);
    return 0;
}

