int N,o,p,x,y,pos,pw3[13]={1},A[1000001],salsa[531441];
char T[200001],sw[1000001];
void OUT(long long x){if(x<0)putchar('-'),x=-x;if(x>=10)OUT(x/10);putchar(x%10+48);}
char* replace(char* s,const char* before,const char* after)
{
  assert(s!=(char*)NULL);assert(before!=(char*)NULL);assert(after!=(char*)NULL);
  const size_t szb=strlen(before),sza=strlen(after);
  char* pt=s;
  if(szb==0)return s;
  for(;;)
  {
    pt=strstr(pt,before);if(pt==(char*)NULL)break;const char* remain=pt+szb;
    memmove(pt+sza,remain,strlen(remain)+1);
    memcpy(pt,after,sza);
    pt+=sza;
  }
  return s;
}
void Dance(char c[])
{
  if(c[p]==EOF)return;
  if(c[p]=='S'){x=salsa[x];if(sw[pos]=='s')pos--;else sw[++pos]='s';}
  if(c[p]=='R'){x++;if(x==pw3[N/2])sw[++pos]='r',x=0;}
}
void Update(void)
{
  rep(i,pw3[N-N/2])
  {
    y=i;
    rep(j,pos){if(sw[j+1]=='s')y=salsa[y];else if(sw[j+1]=='r'){if((++y)==pw3[N-N/2])y=0;}}
    A[o+i*pw3[N/2]]=y*pw3[N/2]+x;
  }
}
