void update(int q,int &last,int &d)
{
    if(d!=0&&q!=last%2)d=3-d;
    last=q;
}
pair<vector<int>,vector<int> > solve(int n)
{
    if(!n)
        return make_pair(vector<int>({0}),vector<int>(lt,0));
    auto prev=solve(n-1);
    const auto& p=prev.first,w=prev.second;
    vector<int> new_w(w.size());
    int psiz=p.size();
    vector<int> d(3*psiz);
    for(int i=0;i<3;i++)
    {
        for(int j=0;j<psiz;j++)
        {
            d[i*psiz+j]=i;
        }
    }
    int q=0;
    vector<int> last(3*psiz);
    for(int i=0;i<lt;i++)
    {
        int k=w[i];
        for(int j=k;j<3*psiz;j+=psiz)
        {
            update(q,last[j],d[j]);
            if(d[j]==2)new_w[i]=j;
        }
        if(t[i]=='S')q^=1;
        else
        {
            for(int j=k;j<3*psiz;j+=psiz)d[j]=(d[j]+1)%3;
        }
    }
    vector<int> new_p(3*psiz);
    for(int i=0;i<3*psiz;i++)
    {
        update(q,last[i],d[i]);
        new_p[i]=p[i%psiz]+d[i]*psiz;
    }
    return make_pair(new_p,new_w);
}
