// Function to convert a number from given base 'b'
// to decimal
int toDeci(char *str, int base)
{
    int len = strlen(str);
    int power = 1; // Initialize power of base
    int num = 0;  // Initialize result
    int i;

    // Decimal equivalent is str[len-1]*1 +
    // str[len-2]*base + str[len-3]*(base^2) + ...
    for (i = len - 1; i >= 0; i--)
    {
        // A digit in input number must be
        // less than number's base
        if (val(str[i]) >= base)
        {
            printf("Invalid Number");
            return -1;
        }

        num += val(str[i]) * power;
        power = power * base;
    }

    return num;
}

void rumba() {
    for(int j = 0; j< len; j++ ){
        int num = locations[j];
        int new_num = num +1;
        if(new_num == len){
            new_num = 0;
        }
        locations[j] = new_num;
    }
}

void salsa() {
    for(int j = 0; j< len; j++ ){
        int num = locations[j];
        char res[len];
        char* a = fromDeci(res, 3, num);
        int k = 0;
        while(a[k] != '\0'){
            if((a[k]) == '1'){
                a[k] = '2';
            }else if(a[k] == '2'){
                a[k] = '1';
            } k++;
        }
        int new_num = toDeci(a, 3);
        locations[j] = new_num;
    }
}

