typedef long long ll;
const int maxn = 2e6 + 10;
char str[maxn];
int n, pw[13], tag[maxn], tid[maxn], ch[maxn][3], ans[maxn];

void build(int k, int dep, int val) {
  if (dep == n) return tid[k] = val, void();
  ch[k][0] = k * 3, build(ch[k][0], dep + 1, val);
  ch[k][1] = k * 3 + 1, build(ch[k][1], dep + 1, val + pw[dep]);
  ch[k][2] = k * 3 + 2, build(ch[k][2], dep + 1, val + pw[dep] * 2);
}

void pushdown(int k) {
  if (tag[k]) {
    tag[k] = 0, swap(ch[k][1], ch[k][2]);
    tag[ch[k][0]] ^= 1, tag[ch[k][1]] ^= 1, tag[ch[k][2]] ^= 1;
  }
}

void upd(int k, int dep) {
  if (dep == n) return;
  pushdown(k);
  upd(ch[k][2], dep + 1);
  swap(ch[k][1], ch[k][2]), swap(ch[k][0], ch[k][1]);
}

void print(int k, int dep, int val) {
  if (dep == n) return ans[tid[k]] = val, void();
  pushdown(k);
  print(ch[k][0], dep + 1, val);
  print(ch[k][1], dep + 1, val + pw[dep]);
  print(ch[k][2], dep + 1, val + pw[dep] * 2);
}

void solve() {
  scanf("%d %s", &n, str + 1);
  pw[0] = 1;
  rep(i, 1, n) pw[i] = 3 * pw[i - 1];
  build(1, 0, 0);
  rep(i, 1, strlen(str + 1)) {
    if (str[i] == 'S') {
      tag[1] ^= 1;
    } else {
      upd(1, 0);
    }
  }
  print(1, 0, 0);
  rep(i, 0, pw[n] - 1) printf("%d ", ans[i]);
}

int main() {
//  int T = read();
