namespace n91 {

usize pow3(usize n) {
  usize ret = 1;
  for (const usize i : rep(0, n)) {
    ret *= 3;
  }
  return ret;
}

void main_() {
  //*
  std::ios::sync_with_stdio(false);
  std::cin.tie(nullptr);
  //*/

  /*
  0 -> flip
  1 -> add
  */

  struct node {
    std::vector<u8> seq;
    usize pos;
  };

  const usize nn = scan<usize>();
  const std::string tt = scan<std::string>();

  auto dpt = md_vec(1, node());

  dpt[0].pos = 0;

  for (const auto e : tt) {
    if (e == 'S') {
      dpt[0].seq.push_back(0);
    } else {
      dpt[0].seq.push_back(1);
    }
  }

  static constexpr std::array<usize, 3> flip = {0, 2, 1};

  for (const usize n : rep(0, nn)) {
    auto nxt = md_vec(pow3(n + 1), node());
    const usize mask = pow3(n);
    for (const usize i : rep(0, pow3(n + 1))) {
      const auto &dp = dpt[i % mask];
      auto &nx = nxt[i];
      usize st = i / mask;
      usize fc = 0;
      for (const auto e : dp.seq) {
        switch (e) {
        case 0: {
          st = flip[st];
          fc ^= 1;
        } break;
        case 1: {
          st += 1;
          if (st == 3) {
            st = 0;
            if (fc) {
              nx.seq.push_back(0);
              fc = 0;
            }
            nx.seq.push_back(1);
          }
        } break;
        }
      }
      if (fc) {
        nx.seq.push_back(0);
      }
      nx.pos = st * mask + dp.pos;
    }
    dpt = std::move(nxt);
  }

  for (const auto &e : dpt) {
    std::cout << e.pos << " ";
  }
  std::cout << eoln;
}

} // namespace n91

