// Based on editorial solution
int D[13][550005]; // one digit at a time, with digit 0 representing the ones place
int W[13][550005];
int lastUpdated[13][550005];

int N;

int salsa(int x){
    int temp = x;
    int pow3 = 1;
    int res = 0;
    for(int i = 0; i < N; i ++){
        int k = temp%3;
        temp /= 3;

        if(k == 1){
            res += pow3*2;
        }else if(k == 2){
            res += pow3;
        }
        pow3 *= 3;
    }
    return res;
}

void update(int n, int indx, int cntS){
    if((cntS&1) != (lastUpdated[n][indx]&1)){
        // conduct salsa
        if(D[n][indx] == 1){
            D[n][indx] = 2;
        }else if(D[n][indx] == 2){
            D[n][indx] = 1;
        }
    }
    // updated the lastUpdated index
    lastUpdated[n][indx] = cntS;
}

