using namespace std;

typedef long long LL;

const int N = 9e6;

int n;
struct {
    int v;
    int tag;
} t[N];

void build(int p, int v, int d) {
    if (d > n) return;
    t[p].v = v;
    for (int i = 0; i < 3; i++) {
        build(p * 3 + i, i, d + 1);
    }
}

void pushdown(int p) {

    for (int i = 0; i < 3; i++) {
        t[p * 3 + i].tag += t[p].tag;
    }
    if (t[p].tag & 1 && t[p].v) {
        t[p].v ^= 3;
    }
    t[p].tag = 0;
}

void update(int p, int d) {
    if (d >= n) return;
    for (int i = 0; i < 3; i++) {
        int x = p * 3 + i;
        pushdown(x);

        t[x].v++;
        if (t[x].v == 3) {
            t[x].v = 0;
            update(x, d + 1);
        }
    }
}

int query(int p, int x, int d) {
    if (d > n) return 0;
    pushdown(p);
    int res = t[p].v;
    return query(p * 3 + x % 3, x / 3, d + 1) * 3 + res;
}

