void salsa(ll n,ll& c,ll& d)
{
    if(1&(n^c)){
        d = d == 0 ? 0 : 3 - d;
        c ^= 1;
    }
}

pair<vector<ll>,vector<ll>> calc(ll n,char *T){
    if(n==0){
        vector<ll> P = {0};
        vector<ll> L(Tn, 0);
        return {P, L};
    }
    ll i, j, k;
    auto r = calc(n - 1, T);
    ll N = p3[n-1];
    vector<ll> &P = r.first;
    vector<ll> &L = r.second;
    vector<ll> P2(N * 3, 0), L2(Tn, 0), C(N * 3, 0), D(N * 3, 0);
    fornum(i,0,3){
        fornum(j,0,N){
            D[i * N + j] = i;
        }
    }
    ll snum = 0;
    fornum(i,0,Tn){
        ll l = L[i];
        fornum(j,0,3){
            salsa(snum, C[l + j * N], D[l + j * N]);
            if (D[l + j * N] == 2)
                L2[i] = l + j * N;
        }
        if(T[i]=='S'){
            ++snum;
        }else{
            fornum(j,0,3)
                (D[l + j * N] += 1) %= 3;
        }
    }
    fornum(i,0,N*3){
        salsa(snum, C[i], D[i]);
        P2[i] = P[i%N] + D[i] * N;
        //printf("%lld ", P2[i]);
    }
    //printf(":%lld\n", n);
    return {P2, L2};



}
