        d[i].resize(3 * n);
        REP(j, 3 * n) d[i][j] = j / n;
        n *= 3;
    }

    bool flip = false;
    for(char t: T){
        if(t == 'S') flip = !flip;
        else{
            if(!flip){
                int i0 = 0, n = 1;
                REP(k, N){
                    for(int i = i0; i < 3 * n; i += n){
                        if(d[k][i] == 0) d[k][i] = 1;
                        else if(d[k][i] == 1) d[k][i] = 2;
                        else if(d[k][i] == 2){
                            d[k][i] = 0;
                            i0 = i;
                        }
                    }
                    n *= 3;
                }
            }else{
                int i0 = 0, n = 1;
                REP(k, N){
                    for(int i = i0; i < 3 * n; i += n){
                        if(d[k][i] == 0) d[k][i] = 2;
                        else if(d[k][i] == 2) d[k][i] = 1;
                        else if(d[k][i] == 1){
                            d[k][i] = 0;
                            i0 = i;
                        }
                    }
                    n *= 3;
                }
            }
        }
    }
    //mdebug(d);

    n = SZ(d[N - 1]);
    vec ans(n, 0);
    REP(i, n){
        int m = 1;
        REP(k, N){
            int b = d[k][i % (3 * m)];
            if(flip && b >= 1) b = 3 - b;
            ans[i] += m * b;

            m *= 3;
        }
    }
    REP(i, n) cout << ans[i] << ' ';
    cout << endl;

    return 0;
}

