const int M = 5.5e5;
int nxt[2][M], nxt2[2][1005], nxt3[2][1005];
int p[M];
int q[M];
char s[M];
int type[M], cnt[M];
int U = 1, V = 1, W = 1, n, MID, POS;
int bad[1005];
int isBAD[1005];
int perm[1005];

int lft[M], rgt[M];

void get(int l, int r){
	memset(isBAD, 0, sizeof isBAD);
	POS = 0;
	for(int i = 0; i < U; i++){
		int curr = i;
		bool isBad = false;
		for(int j = l; j <= r; j++){
			if(type[j] && curr == U - 1){
				bad[POS++] = i;
				isBAD[i] = true;
				break;
			}
			curr = nxt2[type[j]][curr];

		}
		perm[i] = curr;
	}

	int flipped = (cnt[r] - cnt[l - 1]) & 1;
	for(int i = 0; i < V; i++){
		int L = lft[i], R = rgt[i];
		if(isBAD[R]){
			int curr = i;
			for(int j = l; j <= r; j++) curr = nxt[type[j]][curr];
			q[i] = curr;
		} else{
			q[i] = (flipped ? nxt3[0][L] : L) * U + perm[R];
		}
	}
}

int transform(int v, int k){
	int pwr = 1, ret = 0;
	int V = v;
	for(int i = 0; i < k; i++, pwr *= 3, v /= 3){
		int t = (2 * v) % 3;
		ret += t * pwr;
	}
	return ret;
}
int h[M];
int main(){
	sd(n);
	scanf("%s", s);
	int m = strlen(s);
	for(int i = 1; i <= m; i++){
		type[i] = s[i - 1] == 'R';
		cnt[i] = cnt[i - 1] + 1 - type[i];
	}
	MID = (n + 1) / 2;
	for(int i = 0; i < n; i++) V *= 3;
	for(int i = 0; i < MID; i++) U *= 3;
	for(int i = 0; i < n - MID; i++) W *= 3;

	for(int i = 0; i < V; i++){
		nxt[0][i] = transform(i, n);
		nxt[1][i] = (i + 1) % V;
	}

	for(int i = 0; i < U; i++){
		nxt2[0][i] = transform(i, MID);
		nxt2[1][i] = (i + 1) % U;
	}

	for(int i = 0; i < W; i++){
		nxt3[0][i] = transform(i, n - MID);
		nxt3[1][i] = (i + 1) % W;
	}

	for(int i = 0; i < V; i++){
		lft[i] = i / U;
		rgt[i] = i % U;
		p[i] = i;
	}

	const int SQN = sqrt(n) + 3;

	for(int i = 1; i <= m; i += SQN){
		get(i, min(m + 1, i + SQN) - 1);
		for(int j = 0; j < V; j++) h[j] = q[p[j]];
		memcpy(p, h, sizeof p);
	}

	for(int i = 0; i < V; i++) printf("%d ", p[i]);
}

