	} else {
		for (int j = 0; j < 3; j++) {
			int t = init(depth - 1);
			S[i].go[j] = t;
		}
	}
	return i;
}

void push(int i, int depth) {
	assert(depth);
	if (S[i].mod) {
		swap(S[i].go[1], S[i].go[2]);
		f(t, 3) {
			S[S[i].go[t]].mod ^= 1;
		}
		S[i].mod = 0;
	}
}

void inc(int i, int depth) {
	if (depth) {
		push(i, depth);
		// rotate(S[i].go, S[i].go + 1, S[i].go + 3);
		// reverse(S[i].go, S[i].go + 3);
		tie(S[i].go[0], S[i].go[1], S[i].go[2])
			= mt(S[i].go[2], S[i].go[0], S[i].go[1]);
		inc(S[i].go[0], depth - 1);
	}
}

void extract(int i, int depth, vector<int> &rez) {
	if (!depth) {
		rez.pb(S[i].id);
	} else {
		push(i, depth);
		f(t, 3) {
			extract(S[i].go[t], depth - 1, rez);
		}
	}
}

vector<int> inv(vector<int> p) {
	vector<int> q(le(p));
	for (int i = 0; i < le(p); i++) {
		q[p[i]] = i;
	}
	return q;
}
int comp_l(int i) {
	vector<int> digs;
	f(t, n) {
		digs.pb(i % 3);
		i /= 3;
	}
	reverse(digs.begin(), digs.end());
	int rez = 0;
	for (int x : digs) {
		if (x == 1 || x == 2)
			x = 3 - x;
		rez = rez * 3 + x;
	}
	return rez;
}
