#include <bits/stdc++.h>

using namespace std;

const int MAX_N = 631441;

int n, ptot = 1, ans[MAX_N], triple[MAX_N];
char str[MAX_N];

struct node
{
    int ch[3], val, pos;
    bool tag;
} nodes[MAX_N << 1];

void pushdown(int p)
{
    if (nodes[p].tag)
    {
        swap(nodes[p].ch[1], nodes[p].ch[2]), nodes[p].tag = false;
        for (int i = 0; i < 3; i++)
            if (nodes[p].ch[i])
                nodes[nodes[p].ch[i]].tag ^= 1;
    }
}

void update(int p)
{
    if (p == 0)
        return;
    pushdown(p);
    int tmp = nodes[p].ch[2];
    nodes[p].ch[2] = nodes[p].ch[1], nodes[p].ch[1] = nodes[p].ch[0], nodes[p].ch[0] = tmp;
    update(nodes[p].ch[0]);
}

void insert(int x, int ps)
{
    int p = 1;
    for (int i = 0; i < n; i++)
    {
        int b = x % 3;
        if (nodes[p].ch[b] == 0)
            nodes[p].ch[b] = ++ptot;
        p = nodes[p].ch[b], x /= 3;
    }
    nodes[p].pos = ps;
}

void collect(int u, int acc, int dep)
{
    if (dep == n)
        return (void)(ans[nodes[u].pos] = acc);
    pushdown(u);
    for (int i = 0; i < 3; i++)
        if (nodes[u].ch[i])
            collect(nodes[u].ch[i], acc + triple[dep] * i, dep + 1);
}

int main()
{
    scanf("%d%s", &n, str + 1);
    int upper = triple[0] = 1;
    for (int i = 1; i <= n; i++)
        upper *= 3, triple[i] = upper;
    for (int i = 0; i < upper; i++)
        insert(i, i);
    for (int i = 1; str[i]; i++)
        if (str[i] == 'S')
            nodes[1].tag ^= 1, pushdown(1);
        else
            update(1);
    collect(1, 0, 0);
    for (int i = 0; i < upper; i++)
        printf("%d ", ans[i]);
    puts("");
    return 0;
}

#include <bits/stdc++.h>

using namespace std;

int flip(int n) {
    vector<int> digits;
    while (n > 0) {
        digits.push_back(n % 3);
        n /= 3;
    }
    reverse(digits.begin(), digits.end());
    int res = 0;
    for (int d: digits) {
        res *= 3;
        if (d == 1) {
            res += 2;
        } else if (d == 2) {
            res += 1;
        }
    }
    return res;
}
int pow(int a, int n) {
    int res = 1;
    for (int i = 0; i < n; ++i)
        res *= a;
    return res;
}
int main() {
    assert(flip(0) == 0);
    assert(flip(1) == 2);
    assert(flip(2) == 1);
    assert(flip(46) == 65);
    assert(flip(65) == 46);
    assert(pow(3, 3) == 27);
    assert(pow(3, 0) == 1);
    int n;
    cin >> n;
    string s;
    cin >> s;
    const int low_bits = min(max(n / 2 + 1, 1), 6);
    const int high_bits = n - low_bits;
    const int n_low = pow(3, low_bits);
    const int n_high = pow(3, high_bits);
    vector<int> salsa(pow(3, max(low_bits, high_bits)));
    for (int i = 0; i < (int) salsa.size(); ++i) {
        salsa[i] = flip(i);
    }
    vector<int> low(n_low);
    vector<vector<int>> kick(n_low);
    iota(low.begin(), low.end(), 0);
    int f = 0;
    for (char c: s) {
        if (c == 'S') {
            ++f;
            for (int i = 0; i < (int) low.size(); ++i) {
                low[i] = salsa[low[i]];
            }
        } else {
            for (int i = 0; i < (int) low.size(); ++i) {
                ++low[i];
                if (low[i] == (int) low.size()) {
                    kick[i].push_back(f);
                    low[i] = 0;
                }
            }
        }
    }
    for (int i = 0; i < pow(3, n); ++i) {
        int a = i / low.size();
        int b = i % low.size();
        int prev_f = 0;
        for (int i = 0; i < (int) kick[b].size(); ++i) {
            if ((kick[b][i] - prev_f) % 2 > 0) {
                a = salsa[a];
            }
            a = (a + 1) % n_high;
            prev_f = kick[b][i];
        }
        if ((f - prev_f) % 2 > 0) {
            a = salsa[a];
        }
        cout << a * n_low + low[b] << " ";
    }
    cout << "\n";
    return 0;
}

