string reduce(string s2){
  string result;
  int last = 0;
  for(int i = 0; i < s2.size(); i++) {
    if(0 < result.size() && result.back() == 'S' && s2[i] == 'S')
      result.pop_back();
    else
      result += s2[i];
  }
  return result;
}

int powp(int n){
  int result = 1;
  for(int i = 0; i < n; i++)
    result *= 3;
  return result;
}

vector<int> solve(int n, string s){
  if(n == 0){
    vector<int> temp;
    temp.push_back(0);
    return temp;
  }
  s = reduce(s);
  int lim = powp(n);
  vector<int> sol(lim);

  for(int i = 0; i < 3; i++){
    int curr = i;
    string s0;
    for(int j = 0; j < s.size(); j++){
      if(s[j] == 'R')
        curr++;
      else {
        if(curr == 2)
          curr = 1;
        else if(curr == 1)
          curr = 2;
      }
      if(s[j] == 'S')
        s0 += "S";
      else if(curr == 3){
        curr = 0;
        s0 += "R";
      }
    }
    vector<int> sol0 = solve(n - 1, s0);
    for(int j = 0; j < lim / 3 ; j++)
      sol[j * 3 + i] = sol0[j] * 3 + curr;
  }
  return sol;
}

