		build(d+1,a[x].ch[i],v+p[d]*i);
	}
}
void pushdown(int x)
{
	if(a[x].tag)
	{
		for(int i=0;i<3;i++)
		{
			swap(a[a[x].ch[i]].ch[1],a[a[x].ch[i]].ch[2]);
			a[a[x].ch[i]].tag^=1;
		}
		a[x].tag=0;
	}
}
void add(int d,int x)
{
	if(d==n)
		return;
	pushdown(x);
	int b[3];
	for(int i=0;i<3;i++)
		b[i]=a[x].ch[i];
	for(int i=0;i<3;i++)
		a[x].ch[i]=b[(i+2)%3];
	add(d+1,a[x].ch[0]);
}
void out(int d,int x,int v)
{
	if(d==n)
	{
		ans[a[x].id]=v;
		return;
	}
	pushdown(x);
	for(int i=0;i<3;i++)
		out(d+1,a[x].ch[i],v+p[d]*i);
}
int main()
{
	scanf("%d%s",&n,s+1);
	m=strlen(s+1);
	for(int i=1;i<=n;i++)
		p[i]=p[i-1]*3;
	build(0,0,0);
	for(int i=1;i<=m;i++)
	{
		if(s[i]=='S')
		{
			swap(a[0].ch[1],a[0].ch[2]);
			a[0].tag^=1;
		}
		if(s[i]=='R')
			add(0,0);
	}
	out(0,0,0);
	for(int i=0;i<p[n];i++)
		printf("%d ",ans[i]);
	return 0;
}

