long long qexp(long long b,long long p){
    long long res=1;
    while (p){
        if (p&1) res=(res*b);
        b=(b*b);
        p>>=1;
    }
    return res;
}

int calc_oppo(int i){
	int pow=1;
	int res=0;

	while (i){
		if (i%3==1){
			res+=pow*2;
		}
		else if (i%3==2){
			res+=pow;
		}

		i/=3;
		pow*=3;
	}

	return res;
}

