#include<bits/stdc++.h>
using namespace std;

const int maxn=13;
const int maxm=550005; 
int n,m;
char s[maxm];
int pw3[maxn],ans[maxm];

namespace Trie{
	const int maxv=maxm*2;
	int rt,tot;
	int nxt[maxv][3],leaf[maxv];
	bool rev[maxv];
	void build(){
		rt=++tot;
		for(int i=0;i<pw3[n];i++){
			int cur=rt;
			for(int j=0;j<n;j++){
				int c=i/pw3[j]%3;
				if(!nxt[cur][c])nxt[cur][c]=++tot;
				cur=nxt[cur][c];
			}
			leaf[cur]=i;
		}
	}
	void apply(int cur){
		swap(nxt[cur][1],nxt[cur][2]);
		rev[cur]^=1;
	}
	void pushdown(int cur){
		if(rev[cur]){
			apply(nxt[cur][0]);
			apply(nxt[cur][1]);
			apply(nxt[cur][2]);
			rev[cur]=0;
		}
	}
	void S(){
		apply(rt);
	} 
	void R(){
		int cur=rt;
		for(int j=0;j<n;j++){
			pushdown(cur);
			int tmp=nxt[cur][2];
			nxt[cur][2]=nxt[cur][1];
			nxt[cur][1]=nxt[cur][0];
			nxt[cur][0]=tmp;
			cur=nxt[cur][0];
		}
	}
	void getans(){
		for(int i=0;i<pw3[n];i++){
			int cur=rt;
			for(int j=0;j<n;j++){
				pushdown(cur);
				cur=nxt[cur][i/pw3[j]%3];
			}
			ans[leaf[cur]]=i;
		}
	}
}

int main(){
	scanf("%d%s",&n,s);
	m=strlen(s);
	pw3[0]=1;
	for(int i=1;i<=n;i++)pw3[i]=3*pw3[i-1];
	Trie::build();
	for(int i=0;i<m;i++){
		if(s[i]=='S')Trie::S();
		else Trie::R();
	}
	Trie::getans();
	for(int i=0;i<pw3[n];i++)printf("%d%c",ans[i],i==pw3[n]-1?'\n':' ');
	return 0;
}

