const int M = 531442;
const int T = 2e5 + 10;
const int N = 30;
int n, a[M], d[N], sum[M], c[M];
char s[T];

int main() {
	R(n); int S = 1;
	F(i, 1, n) S = S * 3;
	F(i, 0, S - 1) {
		a[i] = i; int L = 0;
		for (int x = i; x; d[++ L] = x % 3, x /= 3);
		G(j, L, 1) sum[i] = sum[i] * 3 + (d[j] ? 3 - d[j] : 0);
	}
	scanf("%s", s + 1);
	int len = strlen(s + 1);
	F(i, 1, len)
		if (s[i] == 'S') {
			F(j, 0, S - 1)
