#include <bits/stdc++.h>
using namespace std;

char T[550005];
int salsa2[550005];
int salsaNext[550005];

int N;

int salsa(int x){
    int temp = x;
    int pow3 = 1;
    int res = 0;
    for(int i = 0; i < N; i ++){
        int k = temp%3;
        temp /= 3;

        if(k == 1){
            res += pow3*2;
        }else if(k == 2){
            res += pow3;
        }
        pow3 *= 3;
    }
    return res;
}

int main(){
    scanf("%d", &N);

    scanf(" %s", T);

    int pow3N = 1;
    for(int i = 0; i < N; i ++){
        pow3N *= 3;
    }

    for(int i = 0; i < pow3N; i ++){
        salsa2[i] = salsa(i);
        //printf("salsa2[%d]=%d\n", i, salsa2[i]);
    }

    for(int i = 0; i < pow3N; i ++){
        salsaNext[salsa2[i+1]] = salsa2[i];
    }

    for(int j = 0; j < pow3N; j ++){
        int x = j;
        for(int i = 0; i < pow3N; i ++){
            if(T[i] == 'R'){
                x = (x+1)%pow3N;
            }else if(T[i] == 'S'){
                x = salsa2[x];
            }
        }
        printf("%d ", x);
    }

    return 0;
}

