int n,ans[maxn],len,p3[13];
void solve(int x,int v,vi &a){
    int res[3];
    vi tmp[3];
    for (int i=0;i<3;i++){
        tmp[i].clear();
        int now=i;
        for (int j=0;j<a.size();j++){
            if (a[j]){
                if (now>0) now=3-now;
                tmp[i].pb(1);
                int m=tmp[i].size();
                if (m>=2&&tmp[i][m-1]&&tmp[i][m-2]) tmp[i].resize(m-2);
            } else {
                now++;
                if (now==3){
                    now=0;
                    tmp[i].pb(0);
                }
            }
        }
        res[i]=now;
    }
    for (int i=0;i<p3[n]/p3[x];i++){
        ans[i*p3[x]+v]+=p3[x]*res[i%3];
    }
    if (x<n-1){
        for (int i=0;i<3;i++) solve(x+1,v+i*p3[x],tmp[i]);
    }
}
