{
  o=scanf("%d %s",&N,T+1);
  //replace(T+1,"SS","");if(T[0]=='\n')return 0;
  rep(i,N)pw3[i+1]=3*pw3[i];
  rep(i,pw3[N])salsa[i]=salsa[i/3]*3+(3-i%3)%3;
  rep(i,pw3[N/2]){o=i,x=i,pos=0;rep(j,strlen(T+1)){p=j+1,Dance(T);}Update();}
  rep(i,pw3[N])OUT(A[i]),putchar(' ');
}

#include <bits/stdc++.h>
using namespace std;

const int maxN = 2e6 + 10;

int n, len, cnt, root;
int ch[maxN + 1][3], a[maxN + 1];
int rev[maxN + 1], pw[15], ans[maxN + 1];
char s[maxN + 1];

inline void reverse(int x) 
{ 
	if(!x) return;
	swap(ch[x][1], ch[x][2]);
	rev[x] ^= 1;
}

inline void pushdown(int x)
{
	if(!rev[x]) return;
	reverse(ch[x][0]);
	reverse(ch[x][1]);
	reverse(ch[x][2]);
	rev[x] = 0;
}

inline void insert(int &node, int x, int k)
{
	if(!node) node = ++ cnt;
	if(k == n) { a[node] = x; return; }
	int d = x / pw[k] % 3;	
	insert(ch[node][d], x, k + 1);
}

inline void change(int node)
{
	if(!node) return;
	pushdown(node);
	change(ch[node][2]);
	swap(ch[node][1], ch[node][2]); 
	swap(ch[node][0], ch[node][1]);
}

inline void dfs(int node, int k, int x)
{
	if(!node) return;
	if(k == n) { ans[ a[node] ] = x; return; }
	pushdown(node);
	dfs(ch[node][0], k + 1, x);
	dfs(ch[node][1], k + 1, x + pw[k]);
	dfs(ch[node][2], k + 1, x + 2 * pw[k]);
}

int main()
{
	scanf("%d", &n);
	scanf("%s", s + 1);
	len = strlen(s + 1);

	pw[0] = 1;
	for(int i = 1; i <= n; i++) pw[i] = pw[i - 1] * 3;

	for(int i = 0; i < pw[n]; i++) insert(root, i, 0);

	for(int i = 1; i <= len; i++)
		if(s[i] == 'S') reverse(root);
		else change(root);

	dfs(root, 0, 0);

	for(int i = 0; i < pw[n]; i++) printf("%d ", ans[i]);
	puts("");
	return 0;
}

