int n, pw[20], s[600100], p[600100], l[600100], lp[600100];

int main() {
  ios_base::sync_with_stdio(false);
  cin.tie(0);
  cout << fixed << setprecision(10);
  cin >> n;
  int ln = (n + 1) / 2;
  pw[0] = 1;
  for (int i = 1; i <= n; i++)
    pw[i] = pw[i - 1] * 3;
  for (int i = 0; i < pw[ln]; i++) {
    int si = 0;
    for (int j = 0; j < ln; j++) {
      int dj = (i / pw[j]) % 3;
      if (dj == 1)
        si += 2 * pw[j];
      else if (dj == 2)
        si += pw[j];
    }
    s[i] = si;
  }
  for (int i = 0; i < pw[n]; i++)
    p[i] = i / pw[ln], l[i] = 0;
  for (int i = 0; i < pw[ln]; i++)
    lp[i] = i;
  char c;
  int cs = 0;
  while (cin >> c) {
    if (c == 'S') {
      cs++;
      for (int i = 0; i < pw[ln]; i++)
        lp[i] = s[lp[i]];
    } else {
      int sv = 0;
      for (int i = 0; i < pw[ln]; i++) {
        lp[i]++;
        if (lp[i] >= pw[ln])
          lp[i] = 0, sv = i;
      }
      for (int i = sv; i < pw[n]; i += pw[ln]) {
        if ((cs - l[i]) & 1)
          p[i] = s[p[i]];
        l[i] = cs;
        p[i]++;
        if (p[i] >= pw[n - ln])
          p[i] = 0;
      }
    }
  }
  for (int i = 0; i < pw[n]; i++)
    if ((cs - l[i]) & 1)
      p[i] = s[p[i]];
  for (int i = 0; i < pw[n]; i++)
    cout << lp[i % pw[ln]] + pw[ln] * p[i] << " ";
  cout << "\n";
}

