        int j = i;
        while (j < t.size() && t[i] == t[j]) j++;
        if (t[i] == 'S' && (j - i) % 2 == 1)  a.pb(1, 1);
        else if (t[i] == 'R') a.pb(0, j - i);
        i = j;
    }
    vec<H>u;
    vec<int>v;
    for (int i = 0; i < a.size();) {
        int j = i, sum = 0;
        while (j < a.size() && a[i].fs == a[j].fs) sum += a[j].sc, j++;
        u.pb(H{ a[i].fs,sum });
        if (a[i].fs == 0) v.pb(sum);
        i = j;
    }
    a = u;//
    crdcomp(v);
    rep(i, min(10ll, (ll)v.size())) {
        //v[i]Sj
        rep(j, n) {
            to3[i][0][j] = to2[(j + v[i]) % n];
        }
        rng(j, 1, 18)rep(k, n) {
            to3[i][j][k] = to3[i][j - 1][to3[i][j - 1][k]];
        }
        d[v[i]] = i + 1;
    }
    vec<int>b(n);
    rep(i, n) b[i] = i;
    for (int i = 0; i < a.size();) {
        vec<int>c(n);
        if (a[i].fs == 1) {
            rep(j, n) c[to2[j]] = b[j];
            b = c;
            i++;
        }
        else {
            if (d[a[i].sc] && i < a.size() - 1) {
                int sum = 0, j = i, r = i;
                for (; i + 1 < a.size() && a[i].sc == a[j].sc; i += 2) {
