                        sum -= (1 << j);
                        b = c;
                    }
                }
            }
            else {
                rep(j, n) c[(j + a[i].sc) % n] = b[j];
                b = c;
                i++;
            }
        }
    }
    vec<int>ans(n);
    rep(i, n) ans[b[i]] = i;
    rep(i, n) cout << ans[i] << ssp(i, n);
