const int maxn = 2e5 + 10;
const int maxp = 531500;
int ans[maxp];
void one(int n)
{
	for (int i = 0; i < (int)pow(3, n); i++)
	{
		int res = 0;
		int three = 1;
		while (ans[i])
		{
			int d = ans[i] % 3;
			if (d == 2)
			{
				res = res + three * 1;
			}
			else if (d == 1)
			{
				res = res + three * 2;
			}
			else if (d == 0)
			{
				res = res + three * 0;
			}
			three *= 3;
			ans[i] /= 3;
		}
		ans[i] = res;
	}
}
void two(int n)
{
	for (int i = 0; i < (int)pow(3, n); i++)
	{
		ans[i] += 1;
		ans[i] %= (int)pow(3, n);
	}
}
int main()
{
	int n;
	char t[maxn];
	cin >> n >> t;
	for (int i = 0; i < (int)pow(3, n); i++)
	{
		ans[i] = i;
	}
	for (int i = 0; t[i]; i++)
	{
		if (t[i] == 'S')
		{
			one(n);
		}
		else if (t[i] == 'R')
		{
			two(n);
		}
	}
	for (int i = 0; i < (int)pow(3, n); i++)
		cout << ans[i] << " ";
	cout << endl;
	return 0;
}

