ll sarusa(ll target,ll n){
    ll result = 0;
    rep(i,n){
        ll powre = pow(3,n-i-1);
        if(target / powre == 1){
            result += powre * 2;
        }
        else if(target / powre == 2){
            result += powre * 1;
        }
        target %= powre;
    }
    return result;
}

