    if(i+1<t.size() && t[i] == 'S' && t[i+1] == 'S'){
      i++;
      continue;
    }
    t_ += t[i];
  }
  return t_;
}

void rec(vector<int>& ans, string& t, vector<int> x, int d, int n){
  if(n == 0){
    return;
  }
  array<vector<int>, 3> type;
  for(int i: x){
    type[(i / d) % 3].push_back(i);
  }
  x = {};

  // f(t, v) -> u
  array<int, 3> mp{0, 1, 2};
  array<int, 3> mp_inv{0, 1, 2};
  array<string, 3> t_{};
  for(int j=0; j<t.size(); j++){
    if(t[j] == 'S'){
      int now_1 = mp_inv[1];
      int now_2 = mp_inv[2];
      swap(mp[now_1], mp[now_2]);
      swap(mp_inv[1], mp_inv[2]);

      for(int k: range(3)){
        if(t_[k].size() && t_[k].back() == 'S'){
          t_[k].pop_back();
        }else{
          t_[k] += 'S';
        }
      }
    }else{
      int now_2 = mp_inv[2];
      t_[now_2] += 'R';

      for(int k: range(3)){
        mp[k] = (mp[k]+1) % 3;
        mp_inv[mp[k]] = k;
      }
    }
  }
  t = "";

  for(int k: range(3)){
    for(int i: type[k]){
      ans[i] += mp[k] * d;
    }
  }
  for(int k: range(3))
    rec(ans, t_[k], type[k], d*3, n-1);
}

