vector<int> toBase3(int x) {
	vector<int> res;
	while (x) {
		res.push_back(x % 3);
		x /= 3;
	}
	return res;
}
int fromBase3(vector<int> &x) {
	int res = 0;
	int base = 1;
	for (int i = 0; i < x.size(); ++i) {
		res += base * x[i];
		base *= 3;
	}
	return res;
}

int flipS(int x) {
	vector<int> b3 = toBase3(x);
	for (int i = 0; i < b3.size(); ++i) {
		b3[i] = (3 - b3[i]) % 3;
	}
	return fromBase3(b3);
}
int flipR(int x) {
	return (x + 1) % md3;
}

