void init() {
	p = vector<vector<int> >(n + 1);
	w = vector<vector<int> >(n + 1);
	int base = 1;
	for (int i = 0; i <= n; ++i) {
		w[i] = vector<int>(t + 1);
		p[i] = vector<int>(base);
		base *= 3;
	}

	p[1][0] = 0; p[1][1] = 1; p[1][2] = 2;
	w[1][0] = 2;
	for (int i = 0; i < t; ++i) {
		if (T[i] == 'S') {
			vectorSingleFlipS(p[1]);
		} else {
			vectorSingleFlipR(p[1]);
		}
		for (int j = 0; j < 3; ++j) {
			if (p[1][j] == 2) {
				w[1][i + 1] = j;
				break;
			}
		}
	}
}

vector<int> upt;
vector<int> sSum;

void relax(int order, int idx, int nowTime) {
	int sTime = sSum[nowTime] - sSum[upt[idx]];
	if (sTime % 2) {
		p[order][idx] = (3 - p[order][idx]) % 3;
	}
	upt[idx] = nowTime;
}

void work() {
	sSum = vector<int>(t + 1);
	sSum[0] = 0;
	for (int i = 1; i <= t; ++i) {
		sSum[i] = sSum[i - 1] + ((T[i - 1] == 'S') ? 1 : 0);
	}
	int base = 3;
	for (int i = 2; i <= n; ++i) {
		upt = vector<int>(p[i].size(), 0);
		// get p[i][j = 1 .. 3^i], w[i][1 .. T]
		// if we have already gotten p[i][j = 1 .. 3^i]

		for (int j = 0; j < p[i].size(); ++j) {
			p[i][j] = j / base;
		}
		w[i][0] = 2;

		for (int j = 0; j < t; ++j) {
			if (T[j] == 'R') {
				for (int ii = 0; ii < 3; ++ii) {
					int idx = ii * base + w[i - 1][j];
					relax(i, idx, j);
					p[i][idx] = (p[i][idx] + 1) % 3;
					upt[idx] += 1;
				}
				for (int ii = 0; ii < 3; ++ii) {
					int idx = ii * base + w[i - 1][j + 1];
					relax(i, idx, j);
					if (p[i][idx] == 2) {
						w[i][j + 1] = ii;
						break;					
					}
				}
			} else {
				// for (int ii = 0; ii < p[i].size(); ++ii) {
				// 	p[i][ii] = (3 - p[i][ii]) % 3;
				// }
				for (int ii = 0; ii < 3; ++ii) {
					int idx = ii * base + w[i - 1][j + 1];
					relax(i, idx, j); 
					p[i][idx] = (3 - p[i][idx]) % 3;
					upt[idx] += 1;
					if (p[i][idx] == 2) {
						w[i][j + 1] = ii;	
						break;				
					}
				}
			}
		}

		for (int j = 0; j <= t; ++j) {
			w[i][j] = w[i][j] * base + w[i - 1][j];
		}
		for (int j = 0; j < p[i].size(); ++j) {
			relax(i, j, t);
			p[i][j] = p[i][j] * base + p[i - 1][j % base];
		}
		base *= 3;

		// cout << "w: ";
		// for (int j = 0; j <= t; ++j) {
		// 	cout << w[i][j] << ' ';
		// }
		// cout << endl;

		// cout << "p: ";
		// for (int j = 0; j < p[i].size(); ++j) {
		// 	cout << p[i][j] << ' ';
		// }
		// cout << endl;
	}
	for (int i = 0; i < p[n].size(); ++i) {
		printf("%d ", p[n][i]);
	}
	putchar('\n');
}

