void build(int p,int f,int num)
{
	//printf("@%d %d %d %d\n",p,f,num,n);
	if(f==n)
	{
		//printf("#%d %d\n",p,num);
		a[p]=num;
		return;
	}
	tr[0][p]=++cnt;
	tr[1][p]=++cnt;
	tr[2][p]=++cnt;
	build(tr[0][p],f+1,num);
	build(tr[1][p],f+1,num+pw3[f]);
	build(tr[2][p],f+1,num+2*pw3[f]);
}
void push(int p)
{
	swap(tr[1][p],tr[2][p]);
	tg[p]=0;
	tg[tr[1][p]]^=1;tg[tr[2][p]]^=1;tg[tr[0][p]]^=1;
}
void opt(int p,int f)
{
	if(f==n)return;
	if(tg[p])push(p);
	swap(tr[1][p],tr[2][p]);
	swap(tr[0][p],tr[1][p]);
	opt(tr[0][p],f+1);
}
void dfs(int p,int f,int num)
{
	if(f==n)
	{
		ans[a[p]]=num;
		return;
	}
	if(tg[p])push(p);
	dfs(tr[0][p],f+1,num);
	dfs(tr[1][p],f+1,num+pw3[f]);
	dfs(tr[2][p],f+1,num+2*pw3[f]);
}
