int n, len, l[3*3*3*3*3*3*3*3*3*3*3*3];
string t;

int calc(int a) {
	if (a < 3) {
		return a % 3 - 1 + (a + 1) % 3;
	}
	else {
		return a % 3 - 1 + (a + 1) % 3 + 3 * calc(a / 3);
	}
}

int main()
{
	cin >> n;
	cin >> t;
	n = pow(3, n);
	for (int i = 0; i < n; i++) {
		l[i] = i;
	}
	for (int i = 0; i < t.size(); i++) {
		if (t[i] == 'S') {
			if (t[i + 1] == 'S') {
				t[i] = '0';
				t[i + 1] = '0';
				i++;
			}
		}
	}
	for (int i = 0; i < t.size(); i++) {
		if (t[i] == 'S') {
			for (int j = 0; j < n; j++) {
				l[j] = calc(l[j]);
			}
		}
		else if(t[i] == 'R'){
			for (int j = 0; j < n; j++) {
				l[j] = (l[j] + 1) % n;
			}

		}
	}
	for (int i = 0; i < n; i++) {
		cout << l[i] << " ";
	}
	cout << endl;
	return 0;
}

