void Solve(int b, const vector <int> &cur, const vector <int> &quer)
{
    if (b >= n || cur.empty()) return;
    vector <int> C[3], Q[3];
    int my[3] = {0, 1, 2};
    int lst = 0;
    for (int i = 0; i < quer.size(); i++) {
        if (sum[quer[i]] % 2 != sum[lst] % 2) swap(my[1], my[2]);
        Q[my[2]].push_back(quer[i]);
        int tmp = my[2]; my[2] = my[1]; my[1] = my[0]; my[0] = tmp;
        lst = quer[i];
    }
    if (sum[T] % 2 != sum[lst] % 2) swap(my[1], my[2]);
    int inv[3];
    for (int i = 0; i < 3; i++)
        inv[my[i]] = i;
    for (int i = 0; i < cur.size(); i++) {
        int val = cur[i] / pw[b] % 3;
        C[val].push_back(cur[i]);
        res[cur[i]] += pw[b] * inv[val];
    }
    for (int i = 0; i < 3; i++)
        Solve(b + 1, C[i], Q[i]);
}

int main()
{
