        else str[i] = 'R';
        sum[i] = sum[i - 1] + (str[i] == 'R');
        if (str[i] == 'S') quer.push_back(i);
    }
    vector <int> cur;
    for (int i = 0; i < pw[n]; i++)
        cur.push_back(i);
    Solve(0, cur, quer);
    for (int i = 0; i < pw[n]; i++)
        printf("%d%c", res[i], i + 1 < pw[n]? ' ': '\n');
    return 0;
}

