const int kMaxSize = 1e6;

int n;
int answer[kMaxSize];
int pwr3;
int cur_digs[kMaxSize];
int ans_digs[kMaxSize];

string Reduce(string T) {
	string ans;
	for (char ch : T) {
		if (ch == 'R') {
			ans += ch;
		} else if (!ans.empty() && ans.back() == 'S') {
			ans.pop_back();
		} else {
			ans += ch;
		}
	}
	return ans;
}

void PutAns() {
	int from = 0, to = 0;
	for (int i = n - 1; i >= 0; --i) {
		from = from * 3 + cur_digs[i];
		to = to * 3 + ans_digs[i];
	}
	answer[from] = to;
}

void Solve(int digit, string T) {
	if (digit == n) {
		return PutAns();
	}

	for (int d : {0, 1, 2}) {
		int cur_d = d;
		string next_t;
		for (char ch : T) {
			if (ch == 'S') {
				next_t += ch;
				if (cur_d) { cur_d = 3 - cur_d; }
			} else {
				++cur_d;
				if (cur_d == 3) {
					next_t += ch;
					cur_d = 0;
				}
			}
		}

		cur_digs[digit] = d;
		ans_digs[digit] = cur_d;
		Solve(digit + 1, Reduce(next_t));
	}
}

int main() {
	ios_base::sync_with_stdio(0);
	cin.tie(0);
	cout << fixed << setprecision(11);
	cerr << fixed << setprecision(6);

	string T;
	cin >> n >> T;
	T = Reduce(T);

	pwr3 = 1;
	for (int i = 0; i < n; ++i) {
		pwr3 *= 3;
	}

	Solve(0, T);
	for (int i = 0; i < pwr3; ++i) {
		cout << answer[i] << " ";
	}
	cout << "\n";
}

