using namespace std;

int sch(int n){     
    int out=0,tmp,count=0;
    while(n != 0){
        tmp = n % 3;
        if(tmp==1){
            out += 2*pow(3,count);
        }else if(tmp==2){
            out += pow(3,count);
        }
        count++;
        n /= 3;
    }
    return out;
}

int main(){
    int N,p,size,len;
    string T;
    vector<int> table(N,0);
    cin >> N;
    cin >> T;
    size = int(pow(3,N));
    len = T.length();
    for(int i=0;i<size;i++){
        if(i % 3 == 0){
            table[i] = sch(i);
        }else if(i % 3 == 1){
            table[i] = table[i-1] + 2;
        }else{
            table[i] = table[i-1] - 1;
        }
    }
    //for(int i=0;i<size;i++) cout << i << ' ' <<table[i] << endl;;

    for(int i=0;i<size;i++){
        p=i;
        for(int j=0;j<len;j++){
            if(T[j] == 'S'){
                if(T[j+1] == 'S') j++;
                else p = table[p];
            }else{
                p++;
