void S(int *data){
  int K[N];

  for(int i=0;i<N/3;i++){
    int k = i;
    int tmp = 0;
    int m = 3;
    int q = i*3;

    while(k>0){
      if(k%3==1){
        tmp = tmp +2*m;
      }else if(k%3==2){
        tmp = tmp +1*m;
      }
      k = k/3;
      m = m*3;
    }
    K[tmp] = data[q];
    K[tmp + 1] = data[q+2];
    K[tmp + 2] = data[q+1];
  }
  for(int i=0;i<N;i++)
  data[i] = K[i];
}


