string compress(string t) {
    string ret = "";
    ret.push_back(t[0]);
    for (int i = 1; i < t.length(); i++) {
        if (ret.back() == 'S' && t[i] == 'S') { ret.pop_back(); }
        else { ret.push_back(t[i]); }
    }
    return ret;
}
int N;
string T;
int ans[550000];
void dfs(int n, int in, int out, string t) {
    if (n == N) {
        ans[in] = out;
        return;
    }
    t = compress(t);
    int c0 = 0, c1 = 1, c2 = 2;
    string v0 = "", v1 = "", v2 = "";
    rep(i, t.size()) {
        if (t[i] == 'S') {
            c0 = (3 - c0) % 3, c1 = (3 - c1) % 3, c2 = (3 - c2) % 3;
            v0.push_back('S'), v1.push_back('S'), v2.push_back('S');
        } else {
            (c0 += 1) %= 3, (c1 += 1) %= 3, (c2 += 1) %= 3;
            if (c0 == 0) { v0.push_back('R'); }
            if (c1 == 0) { v1.push_back('R'); }
            if (c2 == 0) { v2.push_back('R'); }

        }
    }
    int p = my_pow(3, n);
    dfs(n + 1, in + 0 * p, out + c0 * p, v0);
    dfs(n + 1, in + 1 * p, out + c1 * p, v1);
    dfs(n + 1, in + 2 * p, out + c2 * p, v2);
}

