int R(int n, int M) {
    return (n+1) % M;
}

int S(int n, int N) {
    int tmp = 0;
    for (int i=0;i<N;i++) {
        tmp *= 3;
        tmp += (3 - (n%3)) % 3;

        n/=3;
    }

    int ans = 0;
    for (int i=0;i<N;i++) {
        ans *= 3;
        ans += (tmp % 3);

        tmp /=3;
    }

    return ans;
}

int main(){
    ios_base::sync_with_stdio(false);
