    int M = 1;
    for (int i=0;i<N;i++) M*=3;

    vi row(M, 0);
    vvi perms(N, row);

    int tp = 1;
    for (int i=0;i<N;i++) {
        for (int j=0;j<3*tp;j++) {
            perms[i][j] = j/tp;
        }
        tp *= 3;

        // cout << i << ": ";
        // for (int j=0;j<tp;j++) {
        //     cout << perms[i][j] << " ";    
        // }
        // cout << endl;
    }

    bool swap = false;
    int L = T.length();
    for (int i=0;i<L;i++) {
        if (T[i] == 'R') {
            int tp = 1;
            int overflow = 0;
            int nxtof = 0;
            for (int j=0;j<N;j++) {
                for (int k=0;k<3;k++) {
                    int cur = (overflow + k*tp) % (3*tp);
                    if (!swap) {
                        perms[j][cur] = (perms[j][cur] + 1) % 3;
                    } else {
                        perms[j][cur] = (perms[j][cur] + 2) % 3;
                    }

                    if (perms[j][cur] == 0) nxtof = cur;
                }

                overflow = nxtof;
                tp *= 3;
            }
        } else if (T[i] == 'S') {
            if (swap) swap = false;
            else swap = true;
        }
    }

    // cout << "done" << endl;

    // tp = 1;
    // for (int i=0;i<N;i++) {
    //     tp *= 3;

    //     cout << i << ": ";
    //     for (int j=0;j<tp;j++) {
    //         cout << perms[i][j] << " ";    
    //     }
    //     cout << endl;
    // }

    vi salsa{0, 2, 1};

    for (int i=0;i<M;i++) {
        int tp = 1;
        int cur = 0;
        for (int j=0;j<N;j++) {
            if (!swap) cur += perms[j][i % (3*tp)]*tp;
            else cur += salsa[perms[j][i % (3*tp)]]*tp;
            tp *= 3;
        }
        cout << cur << " \n"[i+1==M];
    }

    return 0;
}

