  for (int i = 1; i < num; ++i) {
    vector<int> v;
    int m = i;
    while (m > 0) {
      v.emplace_back(m % 3);
      m /= 3;
    }
    for (int &vi : v)
      if (vi > 0)
        vi = 3 - vi;
    int c = 0;
    for (auto it = v.rbegin(); it != v.rend(); ++it)
      c = c * 3 + *it;
    comp[i] = c;
  }
  vector<pair<char, int>> seq;
  char curr = 'X';
  int cnt = 0;
  t += "!";
  for (char c : t) {
    if (c != curr) {
      if (curr == 'S')
        cnt %= 2;
      if (cnt > 0) {
        if (!seq.empty() && seq.back().first == curr) {
          cnt += seq.back().second;
          seq.pop_back();
        }
        seq.emplace_back(curr, cnt);
      }
      curr = c;
      cnt = 1;
    } else
      cnt++;
  }
  vector<int> ans(num);
  for (int i = 0; i < num; ++i) {
    int pos = i;
    for (auto p : seq) {
      if (p.first == 'S')
        pos = comp[pos];
      else
        pos = (pos + p.second) % num;
    }
    ans[i] = pos;
  }
  for (int i : ans)
    cout << i << " ";
}

