int get1(int mask, char q) {
    if (q == 'R') {
        ++mask; 
        return mask; 
    }   
    int ptr = 1; 
    int ans = 0; 
    while (mask) {
        int t = mask % 3; 
        mask /= 3; 
        if (t == 2) ans += ptr; 
        if (t == 1) ans += 2 * ptr; 
        ptr *= 3; 
    }
    return ans; 
}

int get(int mask, char q) {
    if (q == 'R') {
        ++mask; 
        return mask; 
    }   
    return xyz[mask]; 
}

const int L = 12; 
int pref[maxn]; 
int to[maxn]; 
int kek1[N], kek2[N]; 
int res[N]; 

int get(int l, int r) {
    return pref[r + 1] - pref[l]; 
}

void change(int &mask, int l, int r) {
    if (get(l, r) % 2) {
        mask = get(mask, 'S'); 
    }    
    mask++; 
}

int merge(int left, int right) {
    for (int i = 0; i < 7; ++i) right *= 3;
    return left + right;     
}

int get(int x) {
    vector<int> bts; 
    for (int i = 0; i < L; ++i) {
        bts.pb(x % 3); 
        x /= 3; 
    }
    int left = 0;
    int right = 0; 
    for (int i = 6; i >= 0; --i) {
        left *= 3;
        left += bts[i]; 
    }
    for (int i = L - 1; i >= 7; --i) {
        right *= 3; 
        right += bts[i]; 
    }
    int id = 0; 
    if (left && dp1[left] == -1) {
        return merge(res[left], right);  
    }
    if (left) {    
        id = dp1[left]; 
        change(right, 0, id - 1);             
        left = 0; 
    }                     
    while (next[id] != -1) {
        change(right, id, next[id] - 1);
        id = next[id];                                                                                                                                         
    }
    left = to[id]; 
    return merge(left, right); 
}


void solve() {
    for (int i = 0; i < N; ++i) {
        xyz[i] = get1(i, 'S') % N; 
    }

    cin >> n >> s; 
    int t = s.size(); 
    for (int i = 0; i < N; ++i) {        
        res[i] = i; 
        for (int j = 0; j < t; ++j) {
            if (s[j] == 'S') res[i] = xyz[res[i]]; 
            else res[i] = xyz[res[i]] % N; 
        } 
    }
    for (int i = 0; i < t; ++i) {
        pref[i + 1] = pref[i] + (s[i] == 'S'); 
    }
    for (int i = 0; i < N; ++i) dp1[i] = dp2[i] = -1; 
    next[t] = -1;
    for (int i = 0; i < N; ++i) kek1[i] = i;              
    for (int i = t - 1; i >= 0; --i) {
        for (int mask = 0; mask < N; ++mask) {
            int mask1; 
            if (s[i] == 'S') mask1 = xyz[mask]; 
            else mask1 = mask + 1; 
            mask1 %= N; 
            kek2[mask] = kek1[mask1];
        }
        to[i] = kek2[0]; 
        for (int mask = 0; mask < N; ++mask) {
            int mask1; 
            if (s[i] == 'S') mask1 = xyz[mask]; 
            else mask1 = mask + 1; 
            mask1 %= N;              
            if (mask && mask1 == 0) {
                dp2[mask] = i + 1; 
            } else {
                dp2[mask] = dp1[mask1]; 
            }   
        }
        next[i] = dp2[0]; 
        for (int mask = 0; mask < N; ++mask) {
            kek1[mask] = kek2[mask]; 
            dp1[mask] = dp2[mask]; 
            dp2[mask] = -1; 
        }
    }
    int tet = 1; 
    for (int i = 0; i < n; ++i) tet *= 3; 
    for (int i = 0; i < tet; ++i) {
        cout << get(i) % tet << " "; 
    }
}                                

