void ins(int v) {
    int x=1,p=v;
    for(int i=1;i<=n;i++) {
        int t=v%3;v/=3;
        if(!tr[x][t]) tr[x][t]=++cnt;
        x=tr[x][t];
    }id[x]=p;
}

void push(int x) {tag[x]^=1,swap(tr[x][1],tr[x][2]);}

void pushdown(int x) {
    if(tag[x]) {
        for(int i=0;i<3;i++) push(tr[x][i]);
        tag[x]=0;
    }
}

void dfs(int x) {
    if(!tr[x][0]) return ;
    pushdown(x);
    swap(tr[x][0],tr[x][1]),swap(tr[x][0],tr[x][2]);
    dfs(tr[x][0]);
}

void get(int x,int v,int t) {
    if(!tr[x][0]) {
        r[id[x]]=v;
        return ;
    }
    pushdown(x);
    for(int i=0;i<3;i++) get(tr[x][i],v+t*i,t*3);
}

