	for (int i = 0; i < b; i++) {
		ret += (getbit(x, i) + getbit(y, i)) % 3 * pw[i];
	}
	return ret;
}

int cal(int v) {
	int ret = v;
	for (int i = 1; i <= b; i++) {
		int x = getbit(ret, i-1) + a[i][v % pw[i]];
		x %= 3;
		ret += (x - getbit(ret, i-1)) * pw[i-1];
	}
	return ret;
}

int rev(int v) {
	int ret = 0;
	for (int i = 0; i < b; i++) {
		int x = getbit(v, i);
		if (x == 2) ret += pw[i];
		if (x == 1) ret += 2 * pw[i];
	}
	return ret;
}

int main() {
	pw[0] = 1;
	for (int i = 1; i < 15; i++) pw[i] = pw[i-1] * 3;
	scanf("%d", &b); scanf("%s", str+1);
	for (int i = 0; i <= b; i++) {
		a[i] = new int[pw[i]];
		for (int j = 0; j < pw[i]; j++) {
			a[i][j] = 0;
		}
	}
	n = int (strlen(str + 1));
	for (int i = 1; i <= n; i++) tag[i] = tag[i-1] ^ (str[i] == 'S');
	for (int i = 1; i <= n; i++) {
		if (str[i] != 'R') continue;
		int w = 0, nw = 0;
		for (int x = 0; x < b; x++) {
			// w = mod 3^x  3^x 
			for (int v = 0; v < 3; v++) {
				int t = w + pw[x] * v;
				int tt = cal(t);
				if (tag[i] & 1) {
					a[x+1][t] += 2;
					if (getbit(tt, x) == 1) nw = t;
				} else {
					a[x+1][t] += 1;
					if (getbit(tt, x) == 2) nw = t;
				}
			}
			w = nw;
		}
	}
	for (int i = 0; i < pw[b]; i++) {
		if (tag[n]) printf("%d ", rev(cal(i)));
		else printf("%d ", cal(i));
	}
	puts("");
	return 0;
}

